import { createStore } from 'redux';
import { isArray } from '@lxjx/utils';
import { IModelCreator, IModelSchema, IStoreApis } from './types';

const modelInitMatch = /^__MODEL_INIT_.+__$/;

const modelInitAction = (namespace: string, state: any) => ({
  type: `__MODEL_INIT_${namespace.toUpperCase()}__`,
  state,
  namespace,
});

const setStateMatch = /^.+\.setState$/;

const setStateAction = (namespace: string, state: any) => ({
  type: `${namespace}.setState`,
  state,
  namespace,
});

const store = createStore(
  (state: any = {}, { type, ...action }) => {
    if (modelInitMatch.test(type)) {
      const { state: initState, namespace } = action as ReturnType<typeof modelInitAction>;

      return {
        ...state,
        [namespace]: initState,
      };
    }

    if (setStateMatch.test(type)) {
      const { state: patchState, namespace } = action as ReturnType<typeof setStateAction>;

      return {
        ...state,
        [namespace]: {
          ...state[namespace],
          ...patchState,
        },
      };
    }

    return state;
  },
  {},
  // @ts-ignore
  window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__(),
);

function create<S = any, Actions = any>(
  namespace: string,
  model: IModelSchema<S, Actions>,
): IStoreApis<S, Actions> {
  function get() {
    return store.getState()[namespace];
  }

  function set(patch: Partial<S>) {
    store.dispatch(setStateAction(namespace, patch));
  }

  const { state = {}, actions } = model;

  if (actions) {
    const keys = Object.keys(actions);
    if (isArray(keys) && keys.length) {
      keys.forEach(key => {
        const monkey = actions[key];

        actions[key] = (...args) => {
          monkey({ get, set }, ...args);
        };
      });
    }
  }

  store.dispatch(modelInitAction(namespace, state));
  // function App(rootState: S = state) {
  //   return rootState;
  // }
  return {
    actions,
  };
}

export default create;
